mapper.sql:
	where 1=1


    	and module_id =  #{moduleId}


    	and module_name =  #{moduleName}


    	and parent_id =  #{parentId}


    	and create_time =  #{createTime}


    	and is_ok =  #{isOk}


    	and action_url =  #{actionUrl}


    	and remark =  #{remark}



		and type =  #{type}


    	and level =  #{level}


    	and is_allowed =  #{isAllowed}


    	and sort =  #{sort}

  	 and type in (1,2)

mapper.select:
select  
 from tb_hz_sys_module where module_id= #{moduleId}
and type in (1,2)

	select count(1) from tb_hz_sys_module where is_ok = 0

    	and module_name like CONCAT('%',#{moduleName},'%')  


    	and type =  #{type}


    	and level =  #{level}

	and type in (1,2)

	select 
	m.*,mo.module_name parentName 
	from tb_hz_sys_module m left join (select * from tb_hz_sys_module t where t.is_ok = 0 and t.type in (1,2)) mo on m.parent_id = mo.module_id
	where m.is_ok = 0 

    	and m.module_name like CONCAT('%',#{moduleName},'%')  


		and m.type =  #{type}


    	and m.level =  #{level}

	and m.type in (1,2)
	order by m.level,m.sort
	limit #{startIndex},#{pageSize}

	select  m.* from tb_hz_sys_module m where m.is_ok = 0 and m.level = #{level}

		and m.type =  #{type}


    	and m.is_allowed =  0

	and m.type in (1,2)
	order by m.sort
